    #[doc = " - @ref RT_ERROR_INVALID_VALUE"]
    #[doc = " - @ref RT_ERROR_MEMORY_ALLOCATION_FAILED"]
    #[doc = " - @ref RT_ERROR_TYPE_MISMATCH"]
    #[doc = ""]
